import { useState, useEffect } from "react";
import EventCard from "../../components/EventCard";
import Select from "../../components/Select";
import { useData } from "../../contexts/DataContext";
import Modal from "../Modal";
import ModalEvent from "../ModalEvent";

import "./style.css";

const PER_PAGE = 9;

const EventList = () => {
  const { data, error } = useData();
  const [type, setType] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);

  // Afficher les données des événements
  console.log("Events Data:", data?.events);

  // Filtrage des événements selon le type sélectionné
  const filteredEvents = (data?.events || [])
    .filter(event => !type || event.type === type)
    .slice((currentPage - 1) * PER_PAGE, currentPage * PER_PAGE);

  // Afficher le type sélectionné et les événements filtrés
  console.log("Type Selected:", type);
  console.log("Filtered Events:", filteredEvents);

  // Calcul du nombre total de pages
  const totalFilteredEvents = (data?.events || [])
    .filter(event => !type || event.type === type).length;
  const pageNumber = Math.ceil(totalFilteredEvents / PER_PAGE);

  // Afficher le nombre de pages et la page actuelle
  console.log("Page Number:", pageNumber);
  console.log("Current Page:", currentPage);

  // Liste des types d'événements
  const typeList = Array.from(new Set(data?.events.map(event => event.type)));
  console.log("Type List:", typeList);

  const changeType = (evtType) => {
    setType(evtType);
    setCurrentPage(1); // Réinitialise la page lorsque le type change
  };

  return (
    <>
      {error && <div>An error occurred</div>}
      {!data ? (
        "loading"
      ) : (
        <>
          <h3 className="SelectTitle">Catégories</h3>
          <Select
            selection={typeList}
            onChange={(value) => changeType(value || null)}
          />
          <div id="events" className="ListContainer">
            {filteredEvents.map((event) => (
              <Modal key={event.id} Content={<ModalEvent event={event} />}>
                {({ setIsOpened }) => (
                  <EventCard
                    onClick={() => setIsOpened(true)}
                    imageSrc={event.cover}
                    title={event.title}
                    date={new Date(event.date)}
                    label={event.type}
                  />
                )}
              </Modal>
            ))}
          </div>
          <div className="Pagination">
            {[...Array(pageNumber)].map((_, n) => (
              <a key={n} href="#events" onClick={() => setCurrentPage(n + 1)}>
                {n + 1}
              </a>
            ))}
          </div>
        </>
      )}
    </>
  );
};

export default EventList;
