import { useEffect, useState, useMemo } from "react";
import { useData } from "../../contexts/DataContext";
import { getMonth } from "../../helpers/Date";
import "./style.scss";

const Slider = () => {
  const { data } = useData();
  const [index, setIndex] = useState(0);

  // Memoize sorted events to avoid unnecessary sorting on each render
  const byDateAsc = useMemo(() => {
    return data?.focus?.length ? [...data.focus].sort((evtA, evtB) =>
      new Date(evtA.date) - new Date(evtB.date)
    ) : [];
  }, [data]);

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex < byDateAsc.length - 1 ? prevIndex + 1 : 0));
    }, 5000);

    return () => clearInterval(interval);
  }, [byDateAsc.length]);

  if (!byDateAsc.length) return null; // Return null if no events to display

  return (
    <div className="SlideCardList" role="region" aria-label="Event Slider">
      {byDateAsc.map((event, idx) => (
        <div
          key={event.id}
          className={`SlideCard SlideCard--${index === idx ? "display" : "hide"}`}
          role="tabpanel"
          aria-hidden={index !== idx}
        >
          <img
            src={event.cover}
            alt={event.title}
            onError={(e) => { e.target.src = 'path_to_placeholder_image'; }}
          />
          <div className="SlideCard__descriptionContainer">
            <div className="SlideCard__description">
              <h3>{event.title}</h3>
              <p>{event.description}</p>
              <div>{getMonth(new Date(event.date))}</div>
            </div>
          </div>
        </div>
      ))}
      <div className="SlideCard__paginationContainer">
        <div className="SlideCard__pagination" role="navigation" aria-label="Slider Pagination">
          {byDateAsc.map((_, radioIdx) => (
            <input
              key={`pagination-${radioIdx}`}
              type="radio"
              name="radio-button"
              checked={index === radioIdx}
              onChange={() => setIndex(radioIdx)}
              readOnly
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default Slider;
